select.where:
is_delete = 0

    and open_id = #{user.openId}


    and account = #{user.account}


    and user_name like concat('%', #{user.userName}, '%')


    and nick_name like concat('%', #{user.nickName}, '%')


    and user_type = #{user.userType}


    and phone = #{user.phone}


    and sex = #{user.sex}


    and status = #{user.status}
