mapper.select:



        and icon = #{webCategory.icon}


        and name like concat('%', #{webCategory.name}, '%')


        and description = #{webCategory.description}


        and is_top = #{webCategory.isTop}


        and is_hot = #{webCategory.isHot}


        and target = #{webCategory.target}


        and url = #{webCategory.url}


        and is_valid = #{webCategory.isValid}


order by id desc
