mapper.select:



        and link_type = #{webFriendlyLink.linkType}


        and name like concat('%', #{webFriendlyLink.name}, '%')


        and logo = #{webFriendlyLink.logo}


        and url = #{webFriendlyLink.url}


        and is_top = #{webFriendlyLink.isTop}


        and is_hot = #{webFriendlyLink.isHot}


        and link_num = #{webFriendlyLink.linkNum}


        and description = #{webFriendlyLink.description}


order by id desc
