mapper.select:



        and data_id = #{webUserComment.dataId}


        and top_id = #{webUserComment.topId}


        and user_id = #{webUserComment.userId}


        and to_user_id = #{webUserComment.toUserId}


        and content = #{webUserComment.content}


        and comment_type = #{webUserComment.commentType}


        and status = #{webUserComment.status}


        and like_num = #{webUserComment.likeNum}


        and comment_num = #{webUserComment.commentNum}


        and is_read = #{webUserComment.isRead}


order by id desc
